import Card from '@/components/Card/Card.vue';
import config from '@/config.js';
import ElIncrementSearch from '@/components/IncrementSearch.vue';
import arrayFunction from '@/js/array.js';
import url from '@/js/api_url.js';
import time from '@/js/status/time.js';
export default {
  name: 'niukeSyukei',
  components: {
    Card: Card,
    ElIncrementSearch: ElIncrementSearch
  },
  data: function data() {
    return {
      data: {
        companyID: "",
        search: {
          model: {
            MemberID: "",
            ServiceDate: "2020-11-18"
          },
          validation: {
            MemberID: [{
              required: true,
              message: '社員名が入力されていません',
              trigger: 'blur'
            }],
            ServiceDate: [{
              required: true,
              message: '運行日が入力されていません',
              trigger: 'blur'
            }]
          }
        },
        isSearch: false,
        isSave: false,
        log: {
          service: [],
          driverID: "",
          driverName: "",
          serviceDate: "",
          deletes: {
            ServiceLogs: [],
            RollCallLogs: []
          }
        },
        meisai: [],
        updateButton: "",
        drivers: [],
        rollcallers: [],
        destinations: [],
        vehicles: [],
        rollcallmethods: [],
        diseases: [],
        instructions: []
      }
    };
  },
  methods: {
    getDiseasesIndex: function getDiseasesIndex(DiseaseID) {
      for (var i = 0; i < this.data.diseases.length; i++) {
        if (this.data.diseases[i].DiseaseID == DiseaseID) {
          return i;
        }
      }
    },
    getDriversIndex: function getDriversIndex(DriverID) {
      for (var i = 0; i < this.data.drivers.length; i++) {
        if (this.data.drivers[i].MemberID == DriverID) {
          return i;
        }
      }
    },
    getDriver: function getDriver(DriverID) {
      return this.data.drivers[this.getDriversIndex(DriverID)];
    },
    changeRollCallRogTurn: function changeRollCallRogTurn(seviceIndex, index, kind) {
      var rollCallLog = this.data.log.service[seviceIndex].RollCallLog;

      if (kind == "up") {
        rollCallLog.splice(index - 1, 2, rollCallLog[index], rollCallLog[index - 1]);
      } else if (kind == "down") {
        rollCallLog.splice(index, 2, rollCallLog[index + 1], rollCallLog[index]);
      }
    },
    changeServiceRogTurn: function changeServiceRogTurn(index, kind) {
      var serviceLog = this.data.log.service;

      if (kind == "up") {
        serviceLog.splice(index - 1, 2, serviceLog[index], serviceLog[index - 1]);
      } else if (kind == "down") {
        serviceLog.splice(index, 2, serviceLog[index + 1], serviceLog[index]);
      }
    },
    addServiceMeisai: function addServiceMeisai() {
      var driver = this.getDriver(this.data.log.driverID);
      var vehicleID = driver.VehicleID;
      var destinationID = driver.DestinationID;
      this.data.log.service.push({
        ServiceLog: {
          DestinationID: destinationID,
          VehicleID: vehicleID,
          CompanyID: this.data.companyID,
          EditorID: this.data.editorID,
          ServiceLogID: -1,
          DriverID: this.data.log.driverID,
          ServiceDate: this.data.log.serviceDate
        },
        RollCallLog: [{
          RollCallLogID: -1,
          CompanyID: this.data.companyID,
          EditorID: this.data.editorID
        }]
      });
    },
    remobeServiceMeisai: function remobeServiceMeisai(ServiceLogIndex) {
      if (window.confirm("削除しますか？")) {
        if (this.data.log.service[ServiceLogIndex].ServiceLog.ServiceLogID != -1) {
          this.data.log.deletes.ServiceLogs.push(this.data.log.service[ServiceLogIndex].ServiceLog);

          for (var i in this.data.log.service[ServiceLogIndex].RollCallLog) {
            this.data.log.deletes.RollCallLogs.push(this.data.log.service[ServiceLogIndex].RollCallLog[i]);
          }
        }

        this.data.log.service.splice(ServiceLogIndex, 1);
      }
    },
    addRollCallMeisai: function addRollCallMeisai(ServiceLogIndex) {
      this.data.log.service[ServiceLogIndex].RollCallLog.push({
        CompanyID: this.data.companyID,
        EditorID: this.data.editorID,
        RollCallLogID: -1
      });
    },
    removeRollCallMeisai: function removeRollCallMeisai(ServiceLogIndex, RollCallLogIndex) {
      if (window.confirm("削除しますか？")) {
        if (this.data.log.service[ServiceLogIndex].RollCallLog[RollCallLogIndex].RollCallLogID != -1) {
          this.data.log.deletes.RollCallLogs.push(this.data.log.service[ServiceLogIndex].RollCallLog[RollCallLogIndex]);
        }

        console.log(this.data.log.deletes);
        this.data.log.service[ServiceLogIndex].RollCallLog.splice(RollCallLogIndex, 1);
      }
    },
    search: function search(initFlag) {
      var _this = this;

      this.$refs['searchForm'].validate(function (valid) {
        if (valid) {
          if (_this.data.isSearch) {
            if (!initFlag) {
              if (!window.confirm("点呼記録が保存されませんがよろしいですか？")) {
                return false;
              }
            }
          }

          _this.init();

          _this.getRollCallLogsInput(initFlag);
        }
      });
    },
    init: function init() {
      this.$set(this.data.log, "service", []);
    },
    initInput: function initInput() {
      if (!window.confirm("点呼記録が保存されませんがよろしいですか？")) {
        return false;
      }

      this.init();
      this.data.isSearch = false;
    },
    getRollCallLogsInput: function getRollCallLogsInput(initFlag) {
      var _this2 = this;

      axios.get(url.ApiUrl().GetRollCallLogInputs([{
        key: "MemberID",
        value: this.data.search.model.MemberID
      }, {
        key: "ServiceDate",
        value: this.data.search.model.ServiceDate
      }])).then(function (response) {
        _this2.data.log.driverID = _this2.data.search.model.MemberID;
        _this2.data.log.driverName = _this2.getDriver(_this2.data.search.model.MemberID).MemberName;
        _this2.data.log.serviceDate = _this2.data.search.model.ServiceDate;
        console.log(response.data);

        if (response.data.length == 0) {
          _this2.addServiceMeisai();
        } else {
          _this2.$set(_this2.data.log, "service", response.data);
        }

        _this2.data.isSearch = true;
        console.log(initFlag);

        if (initFlag) {
          _this2.$store.commit('changeMoveRollCallLogInput', {
            ServiceDate: "",
            MemberID: ""
          });
        }
      }).catch(function (response) {
        _this2.data.isSearch = false;
      });
    },
    save: function save() {
      var _this3 = this;

      this.$refs['logForm'].validate(function (valid) {
        if (valid) {
          for (var i in _this3.data.log.service) {
            var beforDrivingRollCallLogCount = 0;
            var afterDrivingRollCallLogCount = 0;

            for (var k in _this3.data.log.service[i].RollCallLog) {
              if (_this3.data.log.service[i].RollCallLog[k].RollCallKind == 0) {
                beforDrivingRollCallLogCount++;
              } else if (_this3.data.log.service[i].RollCallLog[k].RollCallKind == 1) {
                afterDrivingRollCallLogCount++;
              }
            }

            console.log(beforDrivingRollCallLogCount, afterDrivingRollCallLogCount);

            if (beforDrivingRollCallLogCount >= 2 || afterDrivingRollCallLogCount >= 2) {
              alert("乗務前点呼及び乗務後点呼は2つ以上登録できません");
              return false;
            }
          }

          _this3.data.isSave = true;
          axios.post(url.ApiUrl().RollCallLogInputsSave(), _this3.data.log.service).then(function (response) {
            if (response.data) {
              axios.put(url.ApiUrl().RollCallLogInputsDelete(), _this3.data.log.deletes).then(function (response) {
                if (response.data) {
                  _this3.$message({
                    message: '登録に成功しました',
                    type: 'success'
                  });
                } else {
                  _this3.$message({
                    message: '登録に失敗しました',
                    type: 'error'
                  });
                }

                _this3.data.isSave = false;
              }).catch(function (response) {
                _this3.$message({
                  message: '通信に失敗しました',
                  type: 'error'
                });

                _this3.data.isSave = false;
              });
            } else {
              _this3.$message({
                message: '登録に失敗しました',
                type: 'error'
              });
            }
          }).catch(function (response) {
            _this3.$message({
              message: '通信に失敗しました',
              type: 'error'
            });

            _this3.data.isSave = false;
          });
        }
      });
    }
  },
  created: function created() {
    var _this4 = this;

    this.data.companyID = this.$store.state.login.companyID;
    this.data.editorID = this.$store.state.login.userID;
    this.data.search.model.ServiceDate = time.nowJapanDateInput();
    axios.get(url.ApiUrl().GetRollCallLogInputMasterData([{
      key: "CompanyID",
      value: this.data.companyID
    }])).then(function (response) {
      _this4.$set(_this4.data, "drivers", response.data.Drivers);

      _this4.$set(_this4.data, "rollcallers", response.data.RollCallers);

      _this4.$set(_this4.data, "destinations", response.data.Destinations);

      _this4.$set(_this4.data, "vehicles", response.data.Vehicles);

      _this4.$set(_this4.data, "rollcallmethods", response.data.RollCallMethods);

      _this4.$set(_this4.data, "diseases", response.data.Diseases);

      _this4.$set(_this4.data, "instructions", response.data.Instructions);

      console.log(_this4.data.drivers);

      if (_this4.$store.state.moveRollCallLogInput.ServiceDate != "") {
        _this4.data.search.model.ServiceDate = _this4.$store.state.moveRollCallLogInput.ServiceDate;
        _this4.data.search.model.MemberID = _this4.$store.state.moveRollCallLogInput.MemberID;

        _this4.search(true);
      }
    }).catch(function (response) {
      return console.log(response);
    });
  },
  mounted: function mounted() {},
  watch: {}
};
